function ocultaElementosHeader(){document.querySelector("header").classList.add("header--is-hidden")}function exibeElementosHeader(){document.querySelector("header").classList.remove("header--is-hidden")}function escondeAbas(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("cycle__contents__item--is-active")}function removeBotaoAtivo(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("cycle__tabs__button--is-active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]");let e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(this.window.scrollY<e?ocultaElementosHeader:exibeElementosHeader)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);escondeAbas(),t.classList.add("cycle__contents__item--is-active"),removeBotaoAtivo(),e.target.classList.add("cycle__tabs__button--is-active")})});